import React from "react";
import "./FaceRecognition.css";

const FaceRecognition = ({ imageUrl, box }) => {
  const renderBoxes = () => {
    if (Array.isArray(box)) {
      return box.map((singleBox, index) => {
        const { topRow, rightCol, bottomRow, leftCol } = singleBox;

        return (
          <div
            key={index}
            className='bounding-box'
            style={{ top: topRow, right: rightCol, bottom: bottomRow, left: leftCol }}
          ></div>
        );
      });
    } else if (box) {
      // Render a single box if box is not an array
      const { topRow, rightCol, bottomRow, leftCol } = box;

      return (
        <div
          className='bounding-box'
          style={{ top: topRow, right: rightCol, bottom: bottomRow, left: leftCol }}
        ></div>
      );
    }
    return null;
  };

  return (
    <div className='center ma'>
      <div className="absolute mt2">
        <img id='inputimage' alt='' src={imageUrl} width='700px' height='auto'></img>
        {renderBoxes()}
      </div>
    </div>
  );
}

export default FaceRecognition;
