import React from 'react';
import './FaceRecognition.css';

const FaceRecognition = ({ imageUrl, boxes }) => {
  const renderBoxes = () => {
    return boxes.map((box, index) => {
      const dynamicBoxClass = `dynamic-box-${index}`;

      const boxStyle = {
        top: box.topRow + 'px',
        right: box.rightCol + 'px',
        bottom: box.bottomRow + 'px',
        left: box.leftCol + 'px'
      };

      // Create a dynamic style element to apply specific styles
      const dynamicStyle = document.createElement('style');
      dynamicStyle.innerHTML = `
        .${dynamicBoxClass} {
          ${Object.keys(boxStyle).map(prop => `${prop}: ${boxStyle[prop]};`).join('\n')}
        }
      `;

      // Append the dynamic style element to the head of the document
      document.head.appendChild(dynamicStyle);

      return (
        <div
          key={index}
          className={`bounding-box ${dynamicBoxClass}`}
        ></div>
      );
    });
  };

  return (
    <div className='center ma'>
      <div className="absolute mt2">
        <img id='inputimage' alt='' src={imageUrl} width='700px' height='auto'></img>
        {renderBoxes()}
      </div>
    </div>
  );
}

export default FaceRecognition;
